import React, { useState, useEffect } from "react";
import { products } from "../server/api";
import "./style.css";
import LogoImg from "../imges/Group 19.png";
import ProductList from "./ProductList";

function App() {
  const [productData, setProductData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const productList = await products();
        setProductData(productList.list);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    }
    fetchData();
  }, []);

  return (
    <div>
      <div className="top-banner">
        <img src={LogoImg} alt="logoImg" className="panda-logo" />
        <div className="top-banner-menu">자유게시판</div>
        <div className="top-banner-menu">중고마켓</div>
        <button className="login-button">로그인</button>
      </div>
      <div className="main-banner">
        <div>
          <h2>베스트 상품</h2>
        </div>
        <div className="second-product">
          <h2>전체 상품</h2>
          <input />
          <button>상품등록하기</button>
          <div>
            <button>최신순</button>
            <button>좋아요순</button>
          </div>
        </div>
        <div className="all-products">
          <ProductList products={productData} />
        </div>
      </div>
    </div>
  );
}

export default App;
