import React from "react";
import { useState, useEffect } from "react";
import { getUsedMarket } from "../server/api";

function App() {
  // 페이지 주소가 "/items" 인지 확인하기 위한 state
  const [currentPage, setCurrentPage] = useState("");

  // useEffect를 사용하여 페이지 주소 변경 시 currentPage state 업데이트
  useEffect(() => {
    setCurrentPage(window.location.pathname);
  }, []);

  // 중고마켓 버튼의 색상 설정
  const marketButtonStyle = {
    color: currentPage === "/items" ? "#3692FF" : "inherit",
  };

  // 상품 정렬 기준 state
  const [sortOption, setSortOption] = useState("최신 순");

  // 베스트 상품 및 전체 상품의 갯수 설정
  const itemsToShow = {
    desktop: currentPage === "/items" ? 12 : 4,
    tablet: currentPage === "/items" ? 6 : 2,
    mobile: currentPage === "/items" ? 4 : 1,
  };

  return (
    <div>
      <div>
        <button style={marketButtonStyle}>중고마켓</button>
        <a href="FreeBoard">자유게시판</a>
        <a href="Items">중고마켓</a>
        <button>로그인</button>
      </div>
      <div>
        <input />
        <button>상품 등록하기</button>
      </div>
      {/* 베스트 상품 */}
      <div>
        <h2>베스트 상품</h2>
        {/* 베스트 상품을 위한 컴포넌트를 만들어 사용 */}
        <BestProducts itemsToShow={itemsToShow} />
      </div>
      {/* 전체 상품 */}
      <div>
        <h2>전체 상품</h2>
        {/* 전체 상품을 위한 컴포넌트를 만들어 사용 */}
        <AllProducts
          itemsToShow={itemsToShow}
          sortOption={sortOption}
          setSortOption={setSortOption}
        />
      </div>
    </div>
  );
}

// 베스트 상품 컴포넌트
function BestProducts({ itemsToShow }) {
  return (
    <div>
      {/* 화면 크기에 따라 다르게 보여주기 */}
      <p>Desktop: {itemsToShow.desktop}개 보이기</p>
      <p>Tablet: {itemsToShow.tablet}개 보이기</p>
      <p>Mobile: {itemsToShow.mobile}개 보이기</p>
    </div>
  );
}

// 전체 상품 컴포넌트
function AllProducts({ itemsToShow, sortOption, setSortOption }) {
  return (
    <div>
      {/* 드롭다운 메뉴로 정렬 기준 선택 */}
      <select
        value={sortOption}
        onChange={(e) => setSortOption(e.target.value)}
      >
        <option value="최신 순">최신 순</option>
        <option value="좋아요 순">좋아요 순</option>
      </select>
      {/* 화면 크기에 따라 다르게 보여주기 */}
      <p>Desktop: {itemsToShow.desktop}개 보이기</p>
      <p>Tablet: {itemsToShow.tablet}개 보이기</p>
      <p>Mobile: {itemsToShow.mobile}개 보이기</p>
    </div>
  );
}

export default App;
