import React, { useState, useEffect } from "react";
import { getUsedMarket } from "../server/api";
import "./style.css";
import LogoImg from "../imges/Group 19.png";

function App() {
  const [currentPage, setCurrentPage] = useState("");
  const [products, setProducts] = useState([]);

  useEffect(() => {
    setCurrentPage(window.location.pathname);
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const data = await getUsedMarket();
      setProducts(data.list);
    } catch (error) {
      console.error("Error fetching used market data:", error);
    }
  };

  const itemsToShow = {
    desktop: currentPage === "/items" ? 12 : 4,
    tablet: currentPage === "/items" ? 6 : 2,
    mobile: currentPage === "/items" ? 4 : 1,
  };

  return (
    <div>
      <div className="top-banner">
        <img src={LogoImg} alt="logoImg" className="panda-logo" />
        <div className="top-banner-menu">자유게시판</div>
        <div className="top-banner-menu">중고마켓</div>
        <button className="login-button">로그인</button>
      </div>
      <div className="main-banner">
        <div>
          <h2>베스트 상품</h2>
          <div className="best-products">
            <BestProducts itemsToShow={itemsToShow} products={products} />
          </div>
        </div>
        <div>
          <h2>전체 상품</h2>
          <input />
          <button>상품등록하기</button>
        </div>
      </div>
    </div>
  );
}

function BestProducts({ itemsToShow, products }) {
  const sortedProducts = products
    .sort((a, b) => a.createdAt.localeCompare(b.createdAt))
    .slice(0, itemsToShow.desktop);

  return (
    <>
      {sortedProducts.map((product) => (
        <div key={product.id}>
          <img src={product.images[0]} alt={product.name} />
          <p>{product.name}</p>
        </div>
      ))}
    </>
  );
}

export default App;
