import React, { useState, useEffect } from "react";
import { items } from "../server/api";
import "./style.css";
import LogoImg from "../imges/Group 19.png";
import Product from "./Product";

function App() {
  const [products, setProducts] = useState([]);
  const [order, setOrder] = useState("createdAt");
  const [sortedItems, setSortedItems] = useState([]);

  const handleNewestClick = () => setOrder("createdAt");
  const handleBestClick = () => setOrder("rating");

  const handleLoad = async () => {
    const { products } = await items();
    setProducts(products);
  };

  useEffect(() => {
    handleLoad();
  }, []);

  useEffect(() => {
    const sorted = [...products].sort((a, b) => {
      if (order === "createdAt") {
        return new Date(b.createdAt) - new Date(a.createdAt);
      } else if (order === "rating") {
        return b.rating - a.rating;
      }
      return 0;
    });
    setSortedItems(sorted);
  }, [products, order]);

  return (
    <div>
      <div className="top-banner">
        <img src={LogoImg} alt="logoImg" className="panda-logo" />
        <div className="top-banner-menu">자유게시판</div>
        <div className="top-banner-menu">중고마켓</div>
        <button className="login-button">로그인</button>
      </div>
      <div className="main-banner">
        <div>
          <h2>베스트 상품</h2>
        </div>
        <div className="second-product">
          <h2>전체 상품</h2>
          <input />
          <button>상품등록하기</button>
          <div>
            <button onClick={handleNewestClick}>최신순</button>
            <button onClick={handleBestClick}>좋아요순</button>
          </div>
        </div>
      </div>
      <div className="product-list">
        {sortedItems.map((product) => (
          <Product key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default App;
