import React, { useState, useEffect } from "react";
import { getUsedMarket } from "../server/api";
import "./style.css";
import LogoImg from "../imges/Group 19.png";
import { Routes, Route, BrowserRouter } from "react-router-dom";
import FreeBoard from "./FreeBoard";

function App() {
  const [currentPage, setCurrentPage] = useState("");
  const [products, setProducts] = useState([]);
  const [sortOption, setSortOption] = useState("최신 순");

  useEffect(() => {
    setCurrentPage(window.location.pathname);
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const data = await getUsedMarket();
      setProducts(data.list);
    } catch (error) {
      console.error("Error fetching used market data:", error);
    }
  };

  const itemsToShow = {
    desktop: currentPage === "/items" ? 12 : 4,
    tablet: currentPage === "/items" ? 6 : 2,
    mobile: currentPage === "/items" ? 4 : 1,
  };

  return (
    <div>
      <div className="top-banner">
        <img src={LogoImg} alt="logoImg" className="panda-logo" />
        <BrowserRouter>
          <Routes>
            <Route path="/자유게시판" element={<FreeBoard />}></Route>
          </Routes>
        </BrowserRouter>
        <div>중고마켓</div>
        <button>로그인</button>
      </div>
      <div>
        <input />
        <button>상품 등록하기</button>
      </div>
      <div>
        <h2>베스트 상품</h2>
        <div className="best-products">
          <BestProducts itemsToShow={itemsToShow} products={products} />
        </div>
      </div>
      <div>
        <h2>전체 상품</h2>
        <div className="all-products">
          <AllProducts
            itemsToShow={itemsToShow}
            sortOption={sortOption}
            setSortOption={setSortOption}
            products={products}
          />
        </div>
      </div>
    </div>
  );
}

function BestProducts({ itemsToShow, products }) {
  const sortedProducts = products
    .sort((a, b) => a.createdAt.localeCompare(b.createdAt))
    .slice(0, itemsToShow.desktop);

  return (
    <>
      {sortedProducts.map((product) => (
        <div key={product.id}>
          <img src={product.images[0]} alt={product.name} />
          <p>{product.name}</p>
        </div>
      ))}
    </>
  );
}

function AllProducts({ itemsToShow, sortOption, setSortOption, products }) {
  const sortedProducts =
    sortOption === "최신 순"
      ? products.sort((a, b) => a.createdAt.localeCompare(b.createdAt))
      : products.sort((a, b) => b.favoriteCount - a.favoriteCount);

  return (
    <>
      <select
        value={sortOption}
        onChange={(e) => setSortOption(e.target.value)}
      >
        <option value="최신 순">최신 순</option>
        <option value="좋아요 순">좋아요 순</option>
      </select>
      {sortedProducts.slice(0, itemsToShow.desktop).map((product) => (
        <div key={product.id}>
          <img src={product.images[0]} alt={product.name} />
          <p>{product.name}</p>
        </div>
      ))}
    </>
  );
}

export default App;
