import React, { useState, useEffect } from "react";
import { items } from "../server/api";
import "./style.css";
import LogoImg from "../imges/Group 19.png";

function Product({ product }) {
  return (
    <div className="product">
      <h3>{product.name}</h3>
      <p>{product.description}</p>
      <p>가격: {product.price}원</p>
      <p>좋아요 수: {product.favoriteCount}</p>
      <img src={product.images[0]} alt={product.name} />
    </div>
  );
}

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await items();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <div className="top-banner">
        <img src={LogoImg} alt="logoImg" className="panda-logo" />
        <div className="top-banner-menu">자유게시판</div>
        <div className="top-banner-menu">중고마켓</div>
        <button className="login-button">로그인</button>
      </div>
      <div className="main-banner">
        <div>
          <h2>베스트 상품</h2>
        </div>
        <div className="second-product">
          <h2>전체 상품</h2>
          <input />
          <button>상품등록하기</button>
        </div>
      </div>
      <div className="product-list">
        {products.map((product) => (
          <Product key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default App;
