export async function getItems(pageSize=12, orderBy='recent'){
    const query = `pageSize=${pageSize}&orderBy=${orderBy}`;
    const response = await fetch(`https://panda-market-api.vercel.app/products?${query}`);
    const body = await response.json();
    return body;
}
