import { useEffect, useState } from "react";

function AddItem() {
  const [values, setValues] = useState({
    price: "",
    intro: "",
    tag: "",
    file: "",
    itemName: "",
  });
  const [preview, setPreview] = useState();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(values);
  };

  useEffect(() => {
    if (!values.file) return;

    const nextPreview = URL.createObjectURL(values.file);
    setPreview(nextPreview);
  }, [values]);

  return (
    <div id="AddItem">
      <div className="container">
        <form className="AddItemForm" onSubmit={handleSubmit}>
          <div className="addItem_header">
            <h2>상품 등록하기</h2>
            <button type="submit" id="addItem_btn">
              등록
            </button>
          </div>
          <h3>상품 이미지</h3>
          <img src={preview} alt="상품 이미지" />
          <input
            type="file"
            value={values.file}
            name="file"
            id="file"
            onChange={handleChange}
          />
          <h3>상품명</h3>
          <input
            type="text"
            value={values.itemName}
            name="itemName"
            id="itemName"
            onChange={handleChange}
          />
          <h3>상품 소개</h3>
          <textarea
            value={values.intro}
            name="intro"
            id="Intro"
            onChange={handleChange}
          ></textarea>
          <h3>판매 가격</h3>
          <input
            type="text"
            value={values.price}
            name="price"
            id="Price"
            onChange={handleChange}
          />
          <h3>태그</h3>
          <input
            type="text"
            value={values.tag}
            name="tag"
            id="Tag"
            onChange={handleChange}
          />
        </form>
      </div>
    </div>
  );
}
export default AddItem;
