import heartIcon from './images/Icon.png'

function ProdictListItem({item}){
    const price = item.price.toLocaleString('ko-KR');
    return(
        <div className='product-item'>
            <img className='product-img' src={item.images} alt={item.name} />
            <p className='product-title'>
                {item.name}
            </p>
            <p className='product-price'>{item.price}원</p>
            <p className='product-like'> <img src={heartIcon} alt="좋아요 이미지"/> {item.favoriteCount}</p>
        </div>
    );
}

function ProductList({items}){
    return(
        <ul className="product-list">
            {items.map((item) => {
              return(
                <li key={item.id}><ProdictListItem item={item} /></li>
              );  
            })}
        </ul>
    );
}

export default ProductList;
