import {getItems} from './Api.js';
import { useState } from "react";
import ProductList from './ProductList.js';

function BasicProducts() {
const [items, setItems] = useState([]);
const [order, setOrder] = useState('createdAt');

const sortedItems = items.sort((a, b) => b[order] - a[order]);

const handleRegSortedClick = () => setOrder('createdAt');
const handleLikeSortedClick = () => setOrder('favoriteCount');


const productsLoad = async ({options}) => {
  const {list} = await getItems(options);
  setItems(list);
}

useEffect(()=>{
  productsLoad({pageSize:12});
}, []);

return (
  <div>
    <h2>전체상품</h2>
    <button onClick={handleRegSortedClick}>최신순</button>
    <button onClick={handleLikeSortedClick}>좋아요순</button>
    <div className='basic-items'>
      <ProductList items={sortedItems} />
    </div>
  </div>
);
}

export default BasicProducts;
